/* sanitizeInputs.js
 * 
 * Author: Nasrullah Taha
 *
 * Desc: contains function to remove HTML and Javascript tags (or any other tags) from user inputs, as users could enter it for malicious purposes.
 *
 * How to use: Require the .js like any other module, with the syntax:
 *   var sanitizeInputs = require('sanitizeInputs.js');
 */


/*
 * Removes all tags from inputString (anything between '<' and '>')
 */
exports.removeTags = function(inputString) {
	return inputString.replace(/\<[ /=\"a-zA-Z0-9.]*\>/gi, "");
}

